"use strict";

document.addEventListener('DOMContentLoaded', function () {
  var darkBtn = document.querySelector('.dark-btn');
  darkBtn.addEventListener('click', function () {
    document.body.classList.toggle('dark');
  });
  var url = encodeURIComponent(window.location.href);
  var shareLinks = {
    facebook: "https://www.facebook.com/sharer/sharer.php?u=".concat(url),
    linkedin: "https://www.linkedin.com/sharing/share-offsite/?url=".concat(url)
  };
  document.querySelector(".share-facebook").addEventListener("click", function () {
    window.open(shareLinks.facebook, "_blank");
  });
  document.querySelector(".share-linkedin").addEventListener("click", function () {
    window.open(shareLinks.linkedin, "_blank");
  });
});

//copy link
document.querySelector('.links__item-copy').addEventListener('click', function () {
  var itemLogo = this.querySelector('.links__item-logo');
  var currentURL = window.location.href;
  navigator.clipboard.writeText(currentURL);
  var newSVG = "\n        <svg xmlns=\"http://www.w3.org/2000/svg\" x=\"0px\" y=\"0px\" width=\"16\" height=\"16\" viewBox=\"0,0,256,256\">\n            <g fill=\"#000055\" fill-rule=\"nonzero\" stroke=\"none\" stroke-width=\"1\" stroke-linecap=\"butt\" stroke-linejoin=\"miter\" stroke-miterlimit=\"10\" stroke-dasharray=\"\" stroke-dashoffset=\"0\" font-family=\"none\" font-weight=\"none\" font-size=\"none\" text-anchor=\"none\" style=\"mix-blend-mode: normal\">\n                <g transform=\"scale(10.66667,10.66667)\">\n                    <path d=\"M12,2c-5.523,0 -10,4.477 -10,10c0,5.523 4.477,10 10,10c5.523,0 10,-4.477 10,-10c0,-5.523 -4.477,-10 -10,-10zM10,17.414l-4.707,-4.707l1.414,-1.414l3.293,3.293l7.293,-7.293l1.414,1.414z\"></path>\n                </g>\n            </g>\n        </svg>\n    ";
  if (itemLogo) {
    itemLogo.innerHTML = newSVG;
  }
});
